}

.notification-block .count {
  position: absolute;
  top: 0;
  right: 0;
  margin-top: -4px;
  margin-right: -5px;
}

.show-gr-dropdown .gr-menu-dropdown {
  top: 110%;
}

.show-gr-dropdown .gr-menu-dropdown.dropdown-right {
  right: 0;
  left: auto;
}

.show-gr-dropdown .gr-menu-dropdown.dropdown-left {
  left: 0;
