}

.show-gr-dropdown:hover .gr-menu-dropdown,
.show-gr-dropdown.show .gr-menu-dropdown {
  opacity: 1;
  top: 95%;
  pointer-events: visible;
}

/* ---------------------------
  01.Component -> { Menu } 
------------------------------*/
.main-menu {
  /* ----------------------
  Custom toggle arrow 
------------------------*/
}

@media (min-width: 992px) {
  .main-menu {
    display: flex;
    justify-content: flex-end;
  }
}

.main-menu .gr-toggle-arrow {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.main-menu .gr-toggle-arrow:after {
  display: none;
}

.main-menu .gr-toggle-arrow i {
  font-size: 24px;
  width: auto;
  line-height: 1;
  height: auto;
  font-weight: 900;
  margin-left: 5px;
  transition: .4s;
}

.main-menu .gr-toggle-arrow:hover i {
  transform: rotate(-180deg);
}

.main-menu>li>.nav-link {
  color: var(--color-headings) !important;
  font-size: 13px;
  font-weight: 700;
  text-transform: uppercase;
  position: relative;
}

@media (min-width: 992px) {
  .main-menu>li>.nav-link {
    padding-top: 35px !important;
    padding-bottom: 33px !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
    margin-left: 18px !important;
    margin-right: 18px !important;
  }

  .main-menu>li>.nav-link:before {
    content: "";
    background: #FA474A;
    width: 100%;
    height: 2px;
    top: 0;
    left: 0;
    transition: transform .4s;
    will-change: transform;
    transform: scaleX(0);
    position: absolute;
  }
}

.main-menu>li>.nav-link:hover:before {
  transform: scaleX(1);
}

@media (min-width: 992px) {
  .main-menu>li>.nav-link.gr-toggle-arrow {
    padding-right: 30px !important;
  }

  .main-menu>li>.nav-link.gr-toggle-arrow:before {
    width: calc(100% - 8px);
  }

  .main-menu>li>.nav-link.gr-toggle-arrow i {
    position: absolute;
    right: -2px;
  }
}

.main-menu>li.active>.nav-link:before {
  transform: scaleX(1);
}

.main-menu>li:hover>.gr-toggle-arrow i {
  transform: rotate(-180deg);
}

/*----- Dropdown styles
-------------------------*/
.gr-menu-dropdown {
  background-color: transparent;
}

@media (min-width: 992px) {
  .gr-menu-dropdown {
    position: absolute;
    min-width: 227px;
    max-width: 227px;
    box-shadow: 0 52px 54px rgba(65, 62, 101, 0.3);
    border-radius: 8px;
    border: 1px solid var(--border-color-3);
    padding: 15px 0px;
    top: 100%;
    z-index: -99;
    opacity: 0;
    transition: opacity .4s, top .4s;
    pointer-events: none;
    left: 0;
    right: auto;
    border-radius: 0 0 10px 10px;
    border: 1px solid var(--border-color-3);
    background-color: #fff;
    display: block;
    border-top: 3px solid #00b074;
  }
}

.gr-menu-dropdown>.drop-menu-item {
  color: var(--color-headings);
  font-size: 13px;
  text-transform: uppercase;
  font-weight: 700;
  padding-left: 30px;
  padding-right: 30px;
  padding-top: 10px;
  padding-bottom: 10px;
}

@media (min-width: 992px) {
  .gr-menu-dropdown>.drop-menu-item {
    color: #2b3940;
  }
}

.gr-menu-dropdown>.drop-menu-item>a {
  color: inherit;
}

@media (min-width: 992px) {
  .gr-menu-dropdown>.drop-menu-item>a {
    transition: .4s;
  }
}

.gr-menu-dropdown>.drop-menu-item:hover>a {
  color: #00b074;
}

.gr-menu-dropdown>.drop-menu-item>.gr-menu-dropdown {
  border-top-color: #00b074;
}

@media (min-width: 992px) {
  .gr-menu-dropdown>.drop-menu-item>.gr-menu-dropdown {
    top: 10px;
    left: auto;
    right: 0;
    opacity: 0;
    transform: translateX(110%);
    transition: .4s;
    pointer-events: none;
    will-change: transform;
  }
}

@media (min-width: 380px) {
  .gr-menu-dropdown>.drop-menu-item>.gr-menu-dropdown>.drop-menu-item {
    padding-left: 25px;
    padding-right: 25px;
  }
}

@media (min-width: 992px) {
  .gr-menu-dropdown>.drop-menu-item>.gr-menu-dropdown>.drop-menu-item {
    padding-left: 30px;
    padding-right: 30px;
  }
}

.gr-menu-dropdown.dropdown-right {
  left: auto;
  right: -90%;
}

@media (min-width: 992px) {
  .gr-menu-dropdown.dropdown-left {
    left: 0 !important;
    right: auto !important;
    transform: translateX(-110%) !important;
  }
}

/*----- Dropdown hover activation related styles
-------------------------------------------------*/
@media (min-width: 992px) {
  .nav-item.dropdown {
    position: relative;
    z-index: 99;
  }
}

@media (min-width: 992px) {
  .nav-item.dropdown:hover>.gr-menu-dropdown {
    top: 90%;
    opacity: 1;
    pointer-events: visible;
  }
}

@media (min-width: 992px) {
  .nav-item.dropdown:hover>.gr-megamenu-dropdown.center {
    transform: translateX(-50%) translateY(-10px);
    z-index: 99;
    opacity: 1;
    pointer-events: visible;
  }
}

@media (min-width: 992px) {

  .nav-item.dropdown:hover>.gr-megamenu-dropdown-2,
  .nav-item.dropdown:hover.triangle-shape:after,
  .nav-item.dropdown:hover.triangle-shape:before {
    transform: translateX(-50%) translateY(-10px);
    z-index: 99;
    opacity: 1;
    pointer-events: visible;
  }
}

.drop-menu-item.dropdown {
  position: relative;
}

.drop-menu-item.dropdown>.gr-toggle-arrow i {
  transform: rotate(-90deg);
}

@media (min-width: 992px) {
  .drop-menu-item.dropdown:hover>.gr-menu-dropdown {
    top: 10px;
    opacity: 1;
    pointer-events: visible;
    transform: translateX(100%);
  }

  .drop-menu-item.dropdown:hover>.gr-menu-dropdown.dropdown-left {
    transform: translateX(-100%) !important;
  }
}

/*-----Mega Dropdown styles
-------------------------*/
.nav-item.dropdown.dropdown-mega {
  z-index: 1;
}

@media (min-width: 992px) {
  .nav-item.dropdown.dropdown-mega {
    position: static;
  }
}

.gr-megamenu-dropdown {
  background-color: transparent;
}

@media (min-width: 992px) {
  .gr-megamenu-dropdown {
    background-color: #fff;
  }
}

.gr-megamenu-dropdown .dropdown-image-block {
  max-height: 336px;
}

@media (min-width: 320px) and (max-width: 992px) {
  .gr-megamenu-dropdown {
    border: 0;
  }
}

@media (min-width: 992px) {
  .gr-megamenu-dropdown {
    padding: 15px;
    min-width: 925px;
    left: 50%;
    transform: translateX(-50%) translateY(10px);
    will-change: transform;
    top: 100%;
    z-index: -99;
    opacity: 0;
    pointer-events: none;
    transition: .4s opacity, .4s transform;
    box-shadow: 0 42px 54px rgba(0, 0, 0, 0.09);
    border: 1px solid #e7e9ed;
    border-radius: 8px;
  }
}

@media (min-width: 1200px) {
  .gr-megamenu-dropdown {
    min-width: 1100px;
  }
}

.gr-megamenu-dropdown .single-dropdown-block .mega-drop-menu-item {
  padding-top: 14px;
  padding-bottom: 14px;
  display: block;
}

.gr-megamenu-dropdown .mega-drop-menu-item {
  padding-top: 2.5px;
  padding-bottom: 2.5px;
  font-size: 13px;
  font-weight: 700;
  text-transform: uppercase;
  transition: .4s;
  color: #2b3940;
}

@media (min-width: 320px) and (max-width: 992px) {
  .gr-megamenu-dropdown .mega-drop-menu-item {
    color: var(--color-headings);
    border-top: 1px solid var(--border-color-3);
    padding-left: 20px;
    padding-bottom: 13px;
    padding-top: 13px;
  }
}

@media (min-width: 992px) {
  .gr-megamenu-dropdown .mega-drop-menu-item {
    padding-left: 10px;
    padding-right: 10px;
  }

  .gr-megamenu-dropdown .mega-drop-menu-item:hover {
    color: #00b074 !important;
  }
}

@media (min-width: 320px) and (max-width: 992px) {
  .gr-megamenu-dropdown [class*="col-"] {
    padding-left: 0;
    padding-right: 0;
  }

  .gr-megamenu-dropdown [class*="row-"] {
    margin-left: 0;
    margin-right: 0;
  }
}

@media (min-width: 320px) and (max-width: 992px) {
  .gr-megamenu-dropdown-2 {
    border: 0;
