------------------------------*/
.search-nav-tab .active {
  background-color: transparent !important;
  border: 0;
}

.search-nav-tab .active>div {
  border-color: #00b074;
}

/* ---------------------------
  01.Component -> { Acccordion } 
------------------------------*/
.accordion-trigger.arrow-icon {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.accordion-trigger.arrow-icon:after {
  content: "\ea05";
  font-family: "Grayic";
  font-size: 20px;
  display: block;
  line-height: 1;
  transform: rotate(0deg);
  transition: .4s;
  color: #00b074;
  top: 2px;
  position: relative;
}

.accordion-trigger.arrow-icon[aria-expanded="true"]:after {
  transform: rotate(-180deg);
}

/* ---------------------------
  01.Component -> { Sidebar } 
------------------------------*/
.dashboard-sidebar-wrapper {
  width: 120;
  height: 100vh;
  position: fixed;
  left: 0;
  top: 0;
  /* background: var(--bg); */
  background: #992b32;
  transform: translateX(-100%);
  transition: .4s transform;
  z-index: 800;
}

@media (min-width: 768px) {
  .dashboard-sidebar-wrapper {
    transform: none;
  }
}

.dashboard-sidebar-wrapper.show {
  transform: translateX(0%);
}

.dashboard-layout-sidebar li a {
  color: #fff;
  position: relative;
  transition: .4s;
  line-height: 1;
  display: flex;
  flex-direction: column;
  max-width: 120px;
  font-size: 12px;
  text-align: center;
  gap: 3px;
}

.dashboard-layout-sidebar li a svg {
  font-size: 24px;
}

[data-theme='dark'] .dashboard-layout-sidebar li a,
.dark-mode-texts .dashboard-layout-sidebar li a {
  color: #fff;
}

.dashboard-layout-sidebar li a:before {
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  width: 4px;
  height: 100%;
  background: #fa474a;
  border-radius: 7px;
  transform: scaleY(0);
  will-change: transform;
  transition: .4s;
}

.dashboard-layout-sidebar li.active {
  background: #f4f5f8;
}

.dashboard-layout-sidebar li a i {
  transition: .4s;
  color: #333;
  min-width: 20px;
  text-align: center;
}

[data-theme='dark'] .dashboard-layout-sidebar li a i,
.dark-mode-texts .dashboard-layout-sidebar li a i {
  color: rgba(210, 220, 214, 0.5);
}

@media (min-width: 992px) {
  .dashboard-layout-sidebar li a:hover {
    color: #fa474a;
  }

  [data-theme='dark'] .dashboard-layout-sidebar li a:hover,
  .dark-mode-texts .dashboard-layout-sidebar li a:hover {
    color: rgba(210, 220, 214, 0.5);
  }

  .dashboard-layout-sidebar li a:hover>i {
    color: #fa474a;
  }

  .dashboard-layout-sidebar li a:hover:before {
    transform: scaleY(1);
  }
}

.dashboard-layout-sidebar li.active a {
  color: #992b32;
  background-color: #fff;
}

[data-theme='dark'] .dashboard-layout-sidebar li.active a,
.dark-mode-texts .dashboard-layout-sidebar li.active a {
  color: rgba(210, 220, 214, 0.5);
}

.dashboard-layout-sidebar li.active a>i {
  color: #fa474a;
}

.dashboard-layout-sidebar li.active a:before {
  transform: scaleY(1);
}

.dashboard-layout-sidebar li.active a svg.mr-5 {
  width: 1.2em;
  height: 1.2em
}

.sidebar-mobile-button {
  min-height: 30px;
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  font-size: 16px;
  font-weight: 600;
  z-index: 801;
  background: #00b074;
  color: #fff !important;
  padding-top: 10px;
  padding-bottom: 10px;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;
}

@media (min-width: 768px) {
  .sidebar-mobile-button {
    transform: translateY(100%);
  }
}

.sidebar-mobile-button:after {
  content: "Menu";
  margin-left: 10px;
}

.sidebar-mobile-button[aria-expanded="true"]:after {
  content: "Sidebar Close";
}

