.sub-stages-container {
  margin-top: 10px;
  margin-bottom: 10px;
}

/* Styling for sub-stage items */
.sub-stage {
  margin-bottom: 5px;
  /* Adjust as needed */
  cursor: pointer;
}

/* Styling for selected sub-stage items */
.sub-stage.selected {
  background-color: #f0f0f0;
  /* Change to desired background color */
}

/* Styling for the checkbox input within sub-stage items */
.sub-stage input[type="checkbox"] {
  margin-top: 5px;
  /* Adjust as needed */
}
