+import React, { useEffect, useRef } from "react";
+const { List } = PSPDFKit.Immutable;
+const { DrawingPoint, Rect } = PSPDFKit.Geometry;
+const { InkAnnotation } = PSPDFKit.Annotations;
+
+export default function PdfViewerComponent(props) {
+  const containerRef = useRef(null);
+
+  useEffect(() => {
+    const loadPSPDFKit = async () => {
+      try {
+        
+        const PSPDFKit = await import("pspdfkit");
+        const container = containerRef.current;
+
+        if (!container) {
+          throw new Error("Container element not found.");
+        }
+
+        const instance = await PSPDFKit.load({
+          container,
+          license: "YOUR_PSPDFKIT_LICENSE_KEY",
+          document: props.document,
+          baseUrl: `${window.location.protocol}//${window.location.host}/${process.env.PUBLIC_URL}`,
+        });
+
+        var annotation = new InkAnnotation({
+          pageIndex: 0,
+          boundingBox: new Rect({ width: 100, height: 100 }),
+          lines: List([
+            List([
+              new DrawingPoint({ x: 0, y: 0 }),
+              new DrawingPoint({ x: 100, y: 100 })
+            ])
+          ])
+        });
+
+        const [createdAnnotation] = await instance.create(annotation);
+        console.log(createdAnnotation); // => '01BS964AM5Z01J9MKBK64F22BQ'
+      } catch (error) {
+        console.error("Error loading PSPDFKit:", error);
+      }
+    };
+
+    loadPSPDFKit();
+
+    return () => {
+      const container = containerRef.current;
+      if (container && window.PSPDFKit) {
+        window.PSPDFKit.unload(container);
+      }
+    };
+  }, [props.document]);
+
+  return <div ref={containerRef} style={{ width: "100%", height: "100vh" }} />;
+}

