+import React, { useEffect, useRef } from "react";
+
+export default function PdfViewerComponent(props) {
+  const containerRef = useRef(null);
+  const instanceRef = useRef(null);
+
+  useEffect(() => {
+    const loadPSPDFKit = async () => {
+      try {
+        const PSPDFKit = await import("pspdfkit");
+        const container = containerRef.current;
+
+        if (!container) {
+          throw new Error("Container element not found.");
+        }
+
+        // Unload previous instance if it exists
+        if (instanceRef.current) {
+          instanceRef.current.unload(container);
+          instanceRef.current = null;
+        }
+
+        const newInstance = await PSPDFKit.load({
+          container,
+          license: "YOUR_PSPDFKIT_LICENSE_KEY",
+          document: props.document,
+          baseUrl: `${window.location.protocol}//${window.location.host}/${process.env.PUBLIC_URL}`,
+        });
+
+        // Store the new instance reference
+        instanceRef.current = newInstance;
+
+        // Event listener for annotation changes
+        newInstance.addEventListener("annotations.change", (event) => {
+          console.log("Annotation changed:", event);
+        });
+
+      } catch (error) {
+        console.error("Error loading PSPDFKit:", error);
+      }
+    };
+
+    loadPSPDFKit();
+
+    return () => {
+      const container = containerRef.current;
+      if (container && instanceRef.current) {
+        instanceRef.current.unload(container);
+        instanceRef.current = null;
+      }
+    };
+  }, [props.document]);
+
+  return <div ref={containerRef} style={{ width: "100%", height: "100vh" }} />;
+}

