+import React, { useEffect, useState } from 'react';
+import PSPDFKit from 'pspdfkit';
+
+function PdfViewer() {
+  const [instance, setInstance] = useState(null);
+  const [comments, setComments] = useState([]);
+  const [newCommentText, setNewCommentText] = useState('');
+  const [replyingToComment, setReplyingToComment] = useState(null);
+
+  useEffect(() => {
+    PSPDFKit.load({
+      // Configuration options
+    }).then((pdfInstance) => {
+      setInstance(pdfInstance);
+
+      pdfInstance.getComments().then((existingComments) => {
+        setComments(existingComments);
+      });
+
+      pdfInstance.addEventListener('comments.change', (newComments) => {
+        setComments(newComments);
+      });
+    });
+
+    return () => {
+      if (instance) {
+        PSPDFKit.unload(/* container */);
+      }
+    };
+  }, []);
+
+  const handleAddComment = () => {
+    if (!newCommentText.trim()) return;
+
+    instance.createComment({
+      text: newCommentText,
+      pageIndex: instance.currentPageIndex,
+    });
+
+    setNewCommentText('');
+  };
+
+  const handleReplyToComment = (commentId) => {
+    setReplyingToComment(commentId);
+  };
+
+  const handleMentionUser = (userId) => {
+    // Logic to mention a user in a comment
+    // For example:
+    const mentionedUser = `@${userId}`;
+    setNewCommentText((prevText) => prevText + mentionedUser + ' ');
+  };
+
+  const handleCancelReply = () => {
+    setReplyingToComment(null);
+  };
+
+  return (
+    <div>
+      <div id="pdf-container" style={{ width: '100%', height: '800px' }}></div>
+
+      <div>
+        <h2>Add Comment</h2>
+        <textarea value={newCommentText} onChange={(e) => setNewCommentText(e.target.value)}></textarea>
+        <button onClick={handleAddComment}>Add Comment</button>
+      </div>
+
+      {replyingToComment && (
+        <div>
+          <h2>Replying to Comment</h2>
+          {/* Display the comment being replied to */}
+          <button onClick={handleCancelReply}>Cancel Reply</button>
+        </div>
+      )}
+
+      <div>
+        <h2>Comments</h2>
+        <ul>
+          {comments.map((comment) => (
+            <li key={comment.id}>
+              <div>
+                <strong>{comment.creatorName}</strong>
+                <p>{comment.text}</p>
+                <button onClick={() => handleReplyToComment(comment.id)}>Reply</button>
+                {/* Add mention option here */}
+              </div>
+            </li>
+          ))}
+        </ul>
+      </div>
+    </div>
+  );
+}
+
+export default PdfViewer;
