+                                : ""
+                      }
+                      onClick={() => {
+                        try {
+                          // setDocId(data.mention_id);
+                          setshow(false);
+                          ReadNotification(data.id);
+                          localStorage.setItem(
+                            "notificationUser",
+                            data.employee_id
+                          );
+                          // setNotificationDoc(1);
+                          // setSelecttDocTypeName("");
