+                                                                    {(data.address || data.city || data.state) && <span
+                                                                        className="font-size-3 text-smoke  mr-7"
+                                                                        title="Current Location"
+                                                                    >
+                                                                        <img
+                                                                            className="mr-1"
+                                                                            height={"16px"}
