+        });
+
+        this.adobeDCView.previewFile({
+            content: {
+                promise: filePromise,
+            },
+            metaData: {
+                fileName: data.name
+            }
+        }, {});
+
+        this.registerSaveApiHandler(); // Register save API handler
+    }
+
+    registerSaveApiHandler() {
