-import React, { useEffect, useState } from 'react';
-
-const AdobePDFViewer = () => {
-  const [view, setView] = useState(null);
-  const urlToPDF =
-    'https://documentcloud.adobe.com/view-sdk-demo/PDFs/Summary.pdf';
-  const fileID = '3A5E36C8AA950DCDEBFBFE46FFCDE0A6'; // The fileID binds the annotations to the document
-  const clientID = 'e800d12fc12c4d60960778b2bc4370af';
-
-  const viewerOptions = {
-    embedMode: 'FULL_WINDOW',
-    defaultViewMode: 'FIT_PAGE',
-    showDownloadPDF: true,
-    showPrintPDF: true,
-    showLeftHandPanel: false,
-    showAnnotationTools: true,
-    enableAnnotationAPIs: true,
-  };
-
-  const annotationManagerConfig = {
-    showToolbar: true,
-    showCommentsPanel: true,
-    downloadWithAnnotations: true,
-    printWithAnnotations: true,
-  };
-
-  useEffect(() => {
-    const adobeDCViewPromise = new Promise((resolve) => {
-      document.addEventListener('adobe_dc_view_sdk.ready', () => {
-        const embeddedView = new AdobeDC.View({
-          clientId: clientID,
-          divId: 'adobe-dc-view',
-        });
-        resolve(embeddedView);
-      });
-    });
-
-    adobeDCViewPromise.then((embeddedView) => {
-      setView(embeddedView);
-    });
-
-    // Clean up event listener
-    return () => {
-      document.removeEventListener('adobe_dc_view_sdk.ready', () => {});
-    };
-  }, [clientID]);
-
-  useEffect(() => {
-    if (view) {
-      fetch(urlToPDF)
-        .then((res) => res.blob())
-        .then((blob) => {
-          const previewPromise = view.previewFile(
-            {
-              // The file content
-              content: { promise: Promise.resolve(blob.arrayBuffer()) },
-              metaData: {
-                fileName: urlToPDF.split('/').slice(-1)[0],
-                id: fileID,
-              },
-            },
-            viewerOptions
-          );
-          createAnnotationManager(previewPromise);
-        });
-    }
-  }, [view, urlToPDF, fileID]);
-
-  function createAnnotationManager(previewPromise) {
-    previewPromise.then((adobeView) => {
-      adobeView.getAnnotationManager().then((annotationManager) => {
-        annotationManager.setConfig(annotationManagerConfig);
-        addAnnotations(annotationManager);
-      });
-    });
-  }
-
-  function addAnnotations(annotationManager) {
-    const annotationURLs = [
-      'https://assets.codepen.io/4479906/underline.json',
-      'https://assets.codepen.io/4479906/highlight.json',
-      'https://assets.codepen.io/4479906/scribble.json',
-    ];
-
-    annotationURLs.forEach((annotURL) => {
-      fetch(annotURL)
-        .then((response) => response.json())
-        .then((json) => {
-          annotationManager.addAnnotations([json]);
-        });
-    });
-  }
-
-  return (
-    <div id="adobe-dc-view" style={{ width: '100%', height: '600px' }}>
-      {/* AdobeDC View will be rendered here */}
-    </div>
-  );
-};
