+    registerSaveApiHandler() {
+        const saveApiHandler = (metaData, content, options) => {
+            return new Promise((resolve, reject) => {
+                setTimeout(() => {
+                    const documentId = metaData.id;
+                    const savedData = JSON.parse(localStorage.getItem(`annotations${documentId}`)) || {};
