+class ViewSDKClient {
+    constructor() {
+        this.readyPromise = new Promise((resolve) => {
+            if (window.AdobeDC) {
+                resolve();
+            } else {
+                document.addEventListener("adobe_dc_view_sdk.ready", () => {
+                    resolve();
+                });
+            }
+        });
+        this.adobeDCView = undefined;
+        this.annots = [];
+    }
+
+    ready() {
+        return this.readyPromise;
+    }
+
+    previewFile(divId, viewerConfig, url, data) {
+        const fileExtension = data.name.split('.').pop().toLowerCase();
+
+        const config = {
+            clientId: "d9e8b7bcb61b42b6a387bfa9cf16a75b",
+        };
+
+        if (divId) {
+            config.divId = divId;
+        }
+
+        this.adobeDCView = new window.AdobeDC.View(config);
+
+        // Set viewer configuration with all options enabled for all file types
+        viewerConfig = {
+            ...viewerConfig,
+            embedMode: window.AdobeDC.View.Enum.EmbedMode.INLINE, // Display inline
+            showAnnotationTools: true, // Show annotation tools
+            showDownloadPDF: true, // Show download PDF option
+            showPrintPDF: true, // Show print PDF option
+            enableFormFilling: true, // Enable form filling
+            showLeftHandPanel: true, // Show left-hand panel
+            showSearchPDF: true, // Show search PDF option
+            showDocumentInfo: true, // Show document information
+        };
+
+        const previewFilePromise = this.adobeDCView.previewFile({
+            content: {
+                location: {
+                    url: url,
+                    fileExtension: fileExtension,
+                    fileType: data.mimeType
+                },
+            },
+            metaData: {
+                fileName: data.name,
+                id: data.id,
+            }
+        }, viewerConfig);
+
+        return previewFilePromise;
+    }
+
+    previewFileUsingFilePromise(divId, filePromise, data) {
+        this.adobeDCView = new window.AdobeDC.View({
+            clientId: "d9e8b7bcb61b42b6a387bfa9cf16a75b",
+            divId,
+        });
+
+        this.adobeDCView.previewFile({
+            content: {
+                promise: filePromise,
+            },
+            metaData: {
+                fileName: data.name
+            }
+        }, {});
+    }
+
+    registerSaveApiHandler() {
+        const saveApiHandler = (metaData, content, options) => {
+            console.log(metaData, content, options);
+            return new Promise(resolve => {
+                setTimeout(() => {
+                    const response = {
+                        code: window.AdobeDC.View.Enum.ApiResponseCode.SUCCESS,
+                        data: {
+                            metaData: Object.assign(metaData, { updatedAt: new Date().getTime() }),
+                            annotationData: this.annots
+                        },
+                    };
+                    console.log(response)
+                    resolve(response);
+                }, 2000);
+            });
+        };
+        this.adobeDCView.registerCallback(
+            window.AdobeDC.View.Enum.CallbackType.SAVE_API,
+            saveApiHandler,
+            {}
+        );
+    }
+    _checkAnnotationBoundingBox(annotation) {
+        console.log("Checking Annotation Bounding Box");
+        console.log(annotation);
+        //copy annotation
+        let annotationCopy = JSON.parse(JSON.stringify(annotation));
+        if (annotation.motivation === "commenting") {
+            const target = annotation.target;
+            const selector = target.selector;
+            if (selector.subtype === "note") {
+                const boundingBox = selector.boundingBox;
+                const x1 = boundingBox[0];
+                const y1 = boundingBox[1];
+                const x2 = boundingBox[2];
+                const y2 = boundingBox[3];
+                const width = x2 - x1;
+                const height = y2 - y1;
+                let annotationChanged  = false;
+                if (width !== DEFAULT_COMMENT_SIZE) {
+                    annotationChanged = true;
+                    annotationCopy.target.selector.boundingBox[2] = x1 + DEFAULT_COMMENT_SIZE;
+                }
+                if (height !== DEFAULT_COMMENT_SIZE) {
+                    annotationCopy.target.selector.boundingBox[3] = y1 + DEFAULT_COMMENT_SIZE;
+                    annotationChanged = true;
+                }
+                if (annotationChanged) {
+                    console.log("Updating Annotation Bounding Box");
+                    console.log(annotationCopy);
+                    this.adobeViewer.getAnnotationManager().then(annotationManager => {
+                        annotationManager.updateAnnotation(annotationCopy).catch((error) => {
+                            console.error("Failed to Update Annotation");
+                            console.error(error);
+                        });
+                    });
+                }
+            }
+        }
+        return annotation;
+    }
+    registerEventsHandler() {
+        this.adobeDCView.registerCallback(
+            window.AdobeDC.View.Enum.CallbackType.EVENT_LISTENER,
+            event => {
+                console.log(event);
+            },
+            {
+                enablePDFAnalytics: true,
+            }
+        );
+    }
+}
