@@ -0,0 +1,80 @@
+import React from 'react';
+import ReactApexChart from 'react-apexcharts';
+
+const DataChart = ({ data }) => {
+  // Extracting counts from data
+  const counts = data.map(item => parseInt(item.count, 10));
+
+  // Extracting statuses from data
+  const statuses = data.map(item => `${item.status === "1"
+    ? "New"
+    : item.status === "2"
+      ? "Prospect"
+      : item.status === "3"
+        ? "Lead"
+        : item.status === "4"
+          ? "Retained"
+          : item.status === "5"
+            ? "Lost"
+            : item.status === "6"
+              ? "Dead"
+              : item.status === "7"
+                ? "Working on"
+                : item.status === "8"
+                  ? "Submitted"
+                  : item.status === "0"
+                    ? "Web"
+                    : "status"}`);
+
+  const options = {
+    chart: {
+      width: '100%', // Change width to 100%
+      type: 'pie', // Change chart type to 'pie'
+      toolbar: {
+        show: false // Hide toolbar
+      }
+    },
+    labels: statuses, // Using extracted statuses as labels
+    fill: {
+      opacity: 1
+    },
+    stroke: {
+      width: 1,
+      colors: undefined
+    },
+    yaxis: {
+      show: false
+    },
+    legend: {
+      position: 'bottom'
+    },
+    plotOptions: {
+      polarArea: {
+        rings: {
+          strokeWidth: 0
+        },
+        spokes: {
+          strokeWidth: 0
+        },
+      }
+    },
+    theme: {
+      monochrome: {
+        enabled: true,
+        shadeTo: 'light',
+        shadeIntensity: 0.6
+      }
+    }
+  };
+
+  return (
+    <div>
+      <div id="chart">
+        <ReactApexChart options={options} series={counts} type="polarArea" width={380} />
+      </div>
+      <div id="html-dist"></div>
+    </div>
+  );
+};
+
+export default DataChart;

