+import React from 'react';
+import ReactApexChart from 'react-apexcharts';
+
+const DataChart = ({ data }) => {
+  // Extracting counts from data
+  const counts = data.map(item => parseInt(item.count, 10));
+
+  // Extracting statuses from data
+  const statuses = data.map(item => `${item.status === "1"
+    ? "New"
+    : item.status === "2"
+      ? "Prospect"
+      : item.status === "3"
+        ? "Lead"
+        : item.status === "4"
+          ? "Retained"
+          : item.status === "5"
+            ? "Lost"
+            : item.status === "6"
+              ? "Dead"
+              : item.status === "7"
+                ? "Working on"
+                : item.status === "8"
+                  ? "Submitted"
+                  : item.status === "0"
+                    ? "Web"
+                    : "status"}`);
+
+
+                    const options = {
+                      chart: {
+                        width: '100%', // Change width to 100%
+                        type: 'pie', // Change chart type to 'pie'
+                        toolbar: {
+                          show: false // Hide toolbar
+                        }
+                      },
+                      labels: statuses, // Using extracted statuses as labels
+                      legend: {
+                        position: 'bottom'
+                      },
+                      plotOptions: {
+                        pie: {
+                          expandOnClick: true, // Allow expanding on click
+                          dataLabels: {
+                            enabled: false // Disable data labels
+                          },
+                          donut: {
+                            labels: {
+                              show: true, // Show labels on donut slices
+                              total: {
+                                show: true, // Show total label
+                                label: 'Total', // Customize total label
+                                formatter: function (w) {
+                                  return w.globals.seriesTotals.reduce((a, b) => {
+                                    return a + b
+                                  }, 0)
+                                }
+                              }
+                            }
+                          }
+                        }
+                      },
+                      colors: ['#FF6633', '#FFB399', '#FF33FF', '#00B3E6',
+                        '#E6B333', '#3366E6', '#999966', '#99FF99', '#B34D4D',
+                        '#80B300', '#809900', '#E6B3B3', '#6680B3', '#66991A',
+                        '#FF99E6', '#CCFF1A', '#FF1A66', '#E6331A', '#33FFCC'],
+                    };
+                    
+                    
+
+
+
+  return (
+    <div className='d=flex justify-content-center'>
+      <div id="chart" className='w-100'>
+        <ReactApexChart options={options} series={counts} type="polarArea" width={"100%"} />
+      </div>
+      <div id="html-dist"></div>
+    </div>
+  );
+};
+
+export default DataChart;

