+                                    <FaRegFilePdf
+                                        style={{
+                                            width: '100px',
+                                            height: '100px',
+                                            color: 'red',
+                                        }}
+                                    />
+                                )}
+                                {item.file && (item.file.mimeType === 'application/msword' || item.file.mimeType === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document') && (
