+                                    <Link onClick={() => setEditNameForm(true)}>
+                                        Edit Name
+                                    </Link>
+                                </li>
+                                {item.folder ? null :
+                                    <li className="list-group-item">
+                                        Update Document
+                                    </li>
+                                }
+                                <li className="list-group-item text-darger">
+                                    Delete
+                                </li>
+                            </ul>}
+                    </>
+                ))}
+            </div></div>
+    )
+}

