+                      state.job_description
+      }, htmlContent);
+    }
+  };
+  console.log(state.job_description)
+  return (
+    <Editor
+      editorState={editorState}
+      onEditorStateChange={handleEditorChange}
+      onBlur={handleBlur}
+    />
+  );
+}

