@@ -0,0 +1,56 @@
+import React, { useState, useEffect } from 'react';
+import { EditorState, convertFromHTML, ContentState } from 'draft-js';
+import { Editor } from 'react-draft-wysiwyg';
+import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
+import { stateToHTML } from 'draft-js-export-html';
+
+export default function TextEditor({ state, setState, name }) {
+  // Text editor for the description
+  const [editorState, setEditorState] = useState(() => EditorState.createEmpty());
+
+  useEffect(() => {
+    const contentBlock = convertFromHTML(
+      name === "description" ? state.description :
+        name === "FollowUp" ? state.remark :
+          name === "emailReplyPage" ? state :
+            name === "addAgentConversation" ? state.message :
+              name === "companyDetails" ? state.about :
+                name === "requirement" ? state.requirement :
+                  name === "yourDuties" ? state.your_duties : state.job_description
+    );
+    const contentState = ContentState.createFromBlockArray(contentBlock);
+    const editorState = EditorState.createWithContent(contentState);
+    setEditorState(editorState);
+  }, [state, name]);
+
+
+  const handleEditorChange = (newEditorState) => {
+    setEditorState(newEditorState);
+  };
+
+  const handleBlur = () => {
+    const htmlContent = stateToHTML(editorState.getCurrentContent());
+    if (name === "emailReplyPage") {
+      setState(htmlContent);
+    } else {
+      setState({
+        ...state,
+        [name]:
+          name === "description" ? state.description :
+            name === "FollowUp" ? state.remark :
+              name === "addAgentConversation" ? state.message :
+                name === "companyDetails" ? state.about :
+                  name === "requirement" ? state.requirement :
+                    name === "yourDuties" ? state.your_duties : state.job_description
+      }, htmlContent);
+    }
+  };
+
+  return (
+    <Editor
+      editorState={editorState}
+      onEditorStateChange={handleEditorChange}
+      onBlur={handleBlur}
+    />
+  );
+}

