+    setpageNo,
+    agentFilterValue,
+    setAgentFilterValue,
+    user_type,
+    adminFilterValue,
+    setAdminFilterValue,
+    interestFilterValue,
+    setinterestFilterValue,
+    setSearchError,
+    skill }) {
+    let [SkillList, setSkillList] = useState([]);
+    let [EducationList, setEducationList] = useState([]);
