+                        }}
+                        className="text-capitalize form-control"
+                    >
+                        <option value="" data-display="Product Designer">
+                            Select Agent
+                        </option>
+                        {(AgentList || []).map((data) => {
+                            return (
+                                <option value={data.id} key={data.id}>
+                                    {data.name}
+                                </option>
+                            );
+                        })}
+                    </select>
+                </div>
+            </div>
+            <div
+                className={
+                    skill === null || skill === undefined
+                        && user_type === "admin" ? "col p-1 form_group mb-3 "
+                        : "d-none"
+                }
