+          setImageAnnotations([]);
+        }
+      } catch (err) {
+        console.log(err);
+        setCommentsList([]);
+        setImageAnnotations([]);
+      }
+    } else {
+      setCommentsList([]);
+      setImageAnnotations([]);
+    }
+  };
+
+  // Generate a list of comments reply
