+          adminid,
+          annotationStatus
+        );
+        if (res.data.status === (1 || "1")) {
+          setCommentsReplyList(res.data.data);
+        }
+      } catch (err) {
+        console.log(err);
+        setCommentsReplyList([]);
+      }
+    } else {
+      setCommentsReplyList([]);
+    }
+  };
+  /*Annotaton functionalites close */
+  /*Functo get Applicants Document */
+  const GetDocument = async (del) => {
