import React,{useState} from 'react'
import { MdOutlineAddTask, MdPersonRemove } from "react-icons/md";
export default function ExecutiveBox({data,index}) {
    const [activeIndex, setActiveIndex] = useState(null);

  const handleToggle = (index) => {
    setActiveIndex(index === activeIndex ? null : index);
  };
  return (
    <div>
          <div className="accordion mt-2" key={index}>
                            <div className="card">
                              <div
                                className="card-header bg-white"
                                id={`headingOne${index}`}
                              >
                                <div className=" d-flex justify-content-between flex-row">
                                  <div className=" d-flex flex-column ">
                                    <div className="d-flex profile_box gx-2">
                                      <div className="media  align-datas-center">
                                        <div className="circle-36 mx-auto overflow-hidden">
                                          {/* {empdata.profile_photo === null ? ( */}
                                          <img
                                            src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460__340.png"
                                            alt=""
                                            className="w-100"
                                          />
                                          {/* ) : (
                                  <img
                                    src={empdata.profile_photo}
                                    alt=""
                                    className="w-100"
                                  />
                                )} */}
                                        </div>
                                      </div>

                                      <h5 className="mb-0">{data.name} </h5>
                                    </div>
                                    <Link to={""} className="text-dark">
                                      {data.contact_no}
                                    </Link>
                                    <Link to={""} className="text-dark">
                                      {data.email}
                                    </Link>
                                  </div>
                                  <div className="btn-group button_group flex-shrink-1">
                                    <button
                                      // onClick={() => AddTask()}
                                      title="Add task"
                                      className={`btn btn-outline-info action_btn ${
                                        index === activeIndex ? "" : "collapsed"
                                      }`}
                                      data-toggle="collapse"
                                      data-target="#collapseOne"
                                      aria-expanded="true"
                                      aria-controls="collapseOne"
                                      onClick={() => handleToggle(index)}
                                    >
                                      <span className="text-gray">
                                        <MdOutlineAddTask />
                                      </span>
                                    </button>
                                    <button
                                      className="btn btn-outline-info action_btn"
                                      onClick={() => RemoveAssined()}
                                      title="Remove Assigned"
                                    >
                                      <span className=" text-danger">
                                        <MdPersonRemove />
                                      </span>
                                    </button>
                                  </div>
                                </div>
                              </div>

                              <div
                                id={`collapseOne${index}`}
                                className={`collapse  ${
                                  index === activeIndex ? "show" : ""
                                }`}
                                aria-labelledby={`headingOne${index}`}
                                data-parent="#accordion"
                              >
                                <div className="card-body d-flex justify-content-between">
                                  <div className="card-text d-flex flex-row">
                                    <div
                                      className={`sub-stage text-capitalize `}
                                      // onClick={() => handleSubStageSelection(expandedStatus, subStage)}
                                    >
                                      <input
                                        type="checkbox"
                                        className="mx-2"
                                        // checked={(selectedStatus || []).some(
                                        //   (data) => data.substage === subStage
                                        // )}
                                        // readOnly
                                      />
                                      Lmia
                                    </div>
                                    <div
                                      className={`sub-stage text-capitalize `}
                                      // onClick={() => handleSubStageSelection(expandedStatus, subStage)}
                                    >
                                      <input
                                        type="checkbox"
                                        className="mx-2"
                                        // checked={(selectedStatus || []).some(
                                        //   (data) => data.substage === subStage
                                        // )}
                                        // readOnly
                                      />
                                      Visa
                                    </div>
                                    <div
                                      className={`sub-stage text-capitalize `}
                                      // onClick={() => handleSubStageSelection(expandedStatus, subStage)}
                                    >
                                      <input
                                        type="checkbox"
                                        className="mx-2"
                                        // checked={(selectedStatus || []).some(
                                        //   (data) => data.substage === subStage
                                        // )}
                                        // readOnly
                                      />
                                      Job
                                    </div>
                                    <div
                                      className={`sub-stage text-capitalize `}
                                      // onClick={() => handleSubStageSelection(expandedStatus, subStage)}
                                    >
                                      <input
                                        type="checkbox"
                                        className="mx-2"
                                        // checked={(selectedStatus || []).some(
                                        //   (data) => data.substage === subStage
                                        // )}
                                        // readOnly
                                      />
                                      Interview
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
    </div>
  )
}
