+import React, { useState } from "react";
+import { AddExecutiveTeam } from "../../api/api";
+import {toast} from "react-toastify"
+export default function Executivelist({ executiveData, selected, manager_id ,setApiCall}) {
+  const [selectedStatus, setSelectedStatus] = useState(selected);
+
+  /*Function to asign memeber to the manager */
+  const HandleAddexecutive = async (Eid, MId) => {
+    console.log(Eid, MId)
+    try {
+      let Response = await AddExecutiveTeam(MId, Eid);
+      if(Response.message === "successfully"){
+        toast.success("Executive asigned successfully", {
+            position: toast.POSITION.TOP_RIGHT,
+            autoClose: 1000,
+          });
+          setApiCall(true);
+      }
+      console.log(Response);
+    } catch (err) {
+      console.log(err);
+    }
+  };
+  return (
+    <div className="card-text row">
+      {(executiveData || []).map((item, index) => {
+        return (
+          <div
+            className={`sub-stage text-capitalize col-md-6`}
+            onClick={() =>
+              HandleAddexecutive(item.admin_id, manager_id)
+            }
+            key={index}
+          >
+            <input
+              type="checkbox"
+              className="mx-2"
+              //   checked={(selectedStatus || []).some(
+              //     (item) => item.substage === subStage
+              //   )}
+              readOnly
+            />
+            {item.name}
+          </div>
+        );
+      })}
+    </div>
+  );
+}

