+import React, { useState } from "react";
+import { AddExecutiveTeam } from "../../api/api";
+import { toast } from "react-toastify";
+export default function Executivelist({
+  executiveData,
+  selected,
+  manager_id,
+  setApiCall,
+}) {
+  const [selectedExecutives, setSelectedExecutives] = useState(selected);
+
+  useEffect(() => {
+    setSelectedExecutives(selected);
+  }, [selected]);
+
+  const toggleSelection = (Eid) => {
+    if (selectedExecutives.includes(Eid)) {
+      // If the executive is already selected, remove it from the list
+      setSelectedExecutives((prevSelected) =>
+        prevSelected.filter((item) => item !== Eid)
+      );
+    } else {
+      // If the executive is not selected, add it to the list
+      setSelectedExecutives((prevSelected) => [...prevSelected, Eid]);
+    }
+  };
+
+
+  /*Function to asign memeber to the manager */
+  const HandleAddexecutive = async (Eid, MId) => {
+    const isSelected = executiveData.some((item) => item.admin_id === Eid);
+    if (isSelected) {
+      try {
+        // setSelectedStatus(
+        //   executiveData.filter((item) => !(item.admin_id === Eid))
+        // );
+        let Response = await AddExecutiveTeam(MId, Eid);
+        if (Response.message === "successfully") {
+          toast.success("Executive asigned successfully", {
+            position: toast.POSITION.TOP_RIGHT,
+            autoClose: 1000,
+          });
+          // Update the selectedStatus state
+          setSelectedStatus((prevSelectedStatus) =>
+            prevSelectedStatus.includes(Eid)
+              ? prevSelectedStatus.filter((item) => item !== Eid)
+              : [...prevSelectedStatus, Eid]
+          );
+          setApiCall(true);
+        }
+        console.log(Response);
+      } catch (err) {
+        console.log(err);
+      }
+    }
+  };
+  return (
+    <div className="card-text row">
+      {(executiveData || []).map((data, index) => {
+        return (
+          <div
+            className={`sub-stage text-capitalize col-md-6`}
+            onClick={() =>{toggleSelection(data.admin_id);
+                 HandleAddexecutive(data.admin_id, manager_id)}}
+            key={index}
+          >
+            <input
+              type="checkbox"
+              className="mx-2"
+            //   checked={(selectedStatus || []).some(
+            //     (item) => item.admin_id === data.admin_id
+            //   )}
+            checked={selectedExecutives.includes(data.admin_id)}
+
+              readOnly
+            />
+            {data.name}
+          </div>
+        );
+      })}
+    </div>
+  );
+}

