function MainLayout() {
  const token = localStorage.getItem("token");
  const userType = localStorage.getItem("userType");
  return (
    <BrowserRouter>
      {/* <CurrentRoute /> */}
      <Routes>
        {/* Employee */}
        <Route path="/" element={<EmployeeHomePage />} />
