import React from 'react'

export default function visaArrowProfile(visaStatus) {
  return (
    <div>{(visaStatus || []).map((item, index) =>{
       
        item.visa_statu === "" ||
              item.visa_statu === null ||
              item.visa_statu === undefined ||
              item.visa_statu === "undefined" ? null : (
              <div
                className={
                  "arrow-steps bg-blue p-1 px-7 col-md-12 d-flex border-right border-bottom "
                }
                key={i}
              >
                <div className="mt-2 job_name text-dark">
                  <span className="m-0 font-size-2 d-block mb-1">
                    {status.job_title}
                  </span>
                </div>
                <div>
                  <div
                    key={i + 1}
                    className={`mt-2 step text-capitalize ${
                      item.visa_statu === "onboarding" ||
                      item.visa_statu === "advertisements" ||
                      item.visa_statu === "documentation" ||
                      item.visa_statu === "candidate placement" ||
                      item.visa_statu === "submission" ||
                      item.visa_statu === "decision"
                        ? "current"
                        : null
                    }`}
                  >
                    <span>onboarding</span>
                  </div>
                  <div
                    key={i + 2}
                    className={`mt-2 step text-capitalize ${
                      item.visa_statu === "documentation" ||
                      item.visa_statu === "advertisements" ||
                      item.visa_statu === "candidate placement" ||
                      item.visa_statu === "submission" ||
                      item.visa_statu === "decision"
                        ? "current"
                        : null
                    }`}
                  >
                    <span>advertisements</span>
                  </div>
                  <div
                    key={i + 3}
                    className={`mt-2 step text-capitalize ${
                      item.visa_statu === "documentation" ||
                      item.visa_statu === "candidate placement" ||
                      item.visa_statu === "submission" ||
                      item.visa_statu === "decision"
                        ? "current"
                        : null
                    }`}
                  >
                    <span>documentation</span>
                  </div>
                  <div
                    key={i + 4}
                    className={`mt-2 step text-capitalize ${
                      item.visa_statu === "candidate placement" ||
                      item.visa_statu === "submission" ||
                      item.visa_statu === "decision"
                        ? "current"
                        : null
                    }`}
                  >
                    <span>candidate placement</span>
                  </div>
                  <div
                    key={i + 5}
                    className={`mt-2 step text-capitalize ${
                      item.visa_statu === "submission" ||
                      item.visa_statu === "decision"
                        ? "current"
                        : null
                    }`}
                  >
                    <span>submission</span>
                  </div>
                  <div
                    key={i + 6}
                    className={`mt-2 step text-capitalize ${
                      item.visa_statu === "decision" ? "current" : null
                    }`}
                  >
                    <span>decision</span>
                  </div>
                </div>
                {/* {JSON.stringify(lmiaStatusRejectComment)} */}
                {/* {lmiaStatusRejectComment[0] != undefined &&
                lmiaStatusRejectComment[0] != "undefined" ? (
                  <div>
                    {(lmiaStatusRejectComment || []).map((item, index) => {
                      return item.job_id;
                    })}
                    {JSON.stringify(lmiaStatusRejectComment)}  ==== {status.job_id}
                  </div>
                ) : (
                  "________"
                )} */}

                {item.visa_statu === "decision" &&
                // lmiaStatusRejectComment[0].job_id === status.job_id &&
                lmiaStatusRejectComment[0] != undefined &&
                lmiaStatusRejectComment[0] != "undefined" ? (
                  <small className="mx-10">
                    {lmiaStatusRejectComment[0].lmia_substage}
                    {lmiaStatusRejectComment[0].lmia_substage === "approved"
                      ? "Congratulation your Limia is Approved"
                      : lmiaStatusRejectComment[0].lmia_substage ===
                        "awaiting decision"
                      ? "Your Limia status is in progress"
                      : lmiaStatusRejectComment[0].lmia_substage === "reject"
                      ? "Sorry to inform you your Limia got rejected."
                      : ""}
                  </small>
                ) : null}
              </div>
    )})}</div>
  )
}
