@@ -0,0 +1,39 @@
+import React from "react";
+import FileViewer from "react-file-viewer"; // Import your FileViewer library
+import { useLocation} from "react-router-dom";
+const PDFViewer = () => {
+    const location = useLocation();
+    const urlSearchParams = new URLSearchParams(location.search);
+    const pdfUrl = urlSearchParams.get("pdfUrl");
+    
+    console.log(decodeURIComponent(pdfUrl));
+  const downloadPDF = () => {
+    const link = document.createElement("a");
+    link.href = pdfUrl;
+    link.target = "_blank";
+    link.download = "resume.pdf";
+    link.click();
+  };
+  if (window.location.pathname === "userpdf") {
+    document.body.classList.remove("admin_body");
+  }
+  return (
+    <div>
+      <h1>Resume</h1>
+      <FileViewer
+        fileType="pdf"
+        filePath={decodeURIComponent(pdfUrl)}
+        errorComponent={() => <div>Error loading document</div>}
+      />
+      <button
+                    className="p-1 rounded-3 btn-info mx-3 w-auto"
+                    onClick={downloadPDF}
+                    title="Download Document"
+                  >
+                    <i className="fa fa-download" aria-hidden="true"></i>
+                  </button>
+    </div>
+  );
+};
+
+export default PDFViewer;

