commits.content:
import React from "react";
import FileViewer from "your-file-viewer-library"; // Import your FileViewer library

const PDFViewer = ({ pdfUrl }) => {
  const downloadPDF = () => {
    const link = document.createElement("a");
    link.href = pdfUrl;
    link.target = "_blank";
    link.download = "resume.pdf";
    link.click();
  };

  return (
    <div>
      <h1>Blank Page with PDF Viewer</h1>
      <FileViewer
        fileType="pdf"
        filePath={pdfUrl}
        errorComponent={() => <div>Error loading document</div>}
      />
      <button onClick={downloadPDF}>Download PDF</button>
    </div>
  );
};

export default PDFViewer;
