                        <Link
                          to="" onClick={() => setFacebook(true)}
                          className="font-size-4 font-weight-semibold position-relative text-white bg-marino h-px-48 flex-all-center w-100 px-6 rounded-5 mb-4 border-0"
                        >
                          <i className="fab fa-facebook-square pos-xs-abs-cl font-size-7 ml-xs-4"></i>
                          <span className="d-none d-xs-block mx-5 px-3">
                            Import from Facebook
                          </span>
                        </Link>
                        {facebook ?
                          <FacebookLogin
                            appId="2170088543184291"
                            autoLoad
                            callback={responseFacebook}
                            fields="name,email,picture"
                            scope="public_profile,user_friends,email,user_actions.books"
                            className="font-size-4 font-weight-semibold position-relative text-white bg-marino h-px-48 flex-all-center w-100 px-6 rounded-5 mb-4"
                            render={renderProps => (
                              <button onClick={renderProps.onClick} className="d-none">
                              </button>
                            )}
                          />
                          : null}
                      </div>
                  </div> */}
                {/* END SOCIAL MEDIA LINK BUTTONS */}
                {/* <div className="or-devider">
                    <span className="font-size-3 line-height-reset">Or</span>
                  </div> */}
                {/* SIGNUP FORM */}
                <form onSubmit={onAgentSignUpClick}>
                  {/* FORM FIELDS */}
                  <div className="form-group">
                    <label
                      htmlFor="email2"
                      className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                    >
                      E-mail
                    </label>
                    <input
                      type="email"
                      name="email"
                      value={"" || state.email}
                      onChange={onInputChange}
                      className={
                        errors.email
                          ? "form-control border border-danger"
                          : "form-control"
                      }
                      placeholder="example@gmail.com"
                      id="email"
                    />
                    {/* ERROR MSG FOR Company EMAIL */}
                    {errors.email && (
                      <span
                        key={errors.email}
                        className="text-danger font-size-3"
                      >
                        {errors.email}
                      </span>
                    )}
                  </div>
                  <div className="form-group">
                    <label
                      htmlFor="contact_no"
                      className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                    >
                      Contact Number
                    </label>
                    <input
                      type="number"
                      name="contact_no"
                      value={"" || state.contact_no}
                      onChange={onInputChange}
                      className={
                        errors.contact_no
                          ? "form-control border border-danger"
                          : "form-control"
                      }
                      placeholder="Contact Number"
                      id="contact_no"
                      min={0}
                    />
                    {/* ERROR MSG FOR Company no */}
                    {errors.contact_no && (
                      <span
                        key={errors.contact_no}
                        className="text-danger font-size-3"
                      >
                        {errors.contact_no}
                      </span>
                    )}
                  </div>
                  <div className="form-group">
                    <label
                      htmlFor="password"
                      className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                    >
                      Password
                    </label>
                    <div className="position-relative">
                      <input
                        name="password"
                        value={"" || state.password}
                        onChange={onInputChange}
                        type="password"
                        className={
                          errors.password
                            ? "form-control border border-danger"
                            : "form-control"
                        }
                        id="password"
                        placeholder="Enter password"
                      />
                      {/* ERROR MSG FOR PASSWORD */}
                      {errors.password && (
                        <span
                          key={errors.password}
                          className="text-danger font-size-3"
                        >
                          {errors.password}
                        </span>
                      )}
                    </div>
                  </div>
                  {otpBox ? (
                    <div className="form-group">
                      <label
                        htmlFor="otp"
                        className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                      >
                        Enter Otp
                      </label>
                      <div className="position-relative">
                        <input
                          type="number"
                          value={"" || state.otp}
                          onChange={onInputChange}
                          maxLength={6}
                          name="otp"
                          id="otp"
                          className={
                            errors.otp
                              ? "form-control border border-danger"
                              : "form-control"
                          }
                          placeholder="Otp"
                          min={0}
                        />
                        {errors.otp && (
                          <span
                            key={errors.otp}
                            className="text-danger font-size-3"
                          >
                            {errors.otp}
                          </span>
                        )}
                      </div>
                    </div>
                  ) : null}
                  {/* END FORM FIELDS  */}
                  <div className=" d-flex flex-wrap justify-content-between mb-1 col-md-12 ">
                    <label
                      htmlFor="term_and_condition"
                      className="gr-check-input d-flex  mr-3"
                    >
                      <input
                        type="checkbox"
                        id="term_and_condition"
                        name="term_and_condition"
                        onChange={(event) =>
                          setState({
                            ...state,
                            term_and_condition: event.target.checked,
                          })
                        }
                        className="text-black-2 pt-5 mr-5"
                      />
                      <span className="font-size-3 mb-0 line-height-reset d-block">
                        Agree to the
                        <Link to="" className="text-primary">
                          Terms & Conditions
                        </Link>
                      </span>
                    </label>
                    {/*----ERROR MESSAGE FOR terms----*/}
                    {errors.term_and_condition && (
                      <span
                        key={errors.term_and_condition}
                        className="text-danger font-size-3"
                      >
                        {errors.term_and_condition}
                      </span>
                    )}
                  </div>
                  {errors.Credentials && (
                    <span
                      key={errors.Credentials}
                      className="text-danger font-size-3"
                    >
                      {errors.Credentials}
                    </span>
                  )}
                  <div className="form-group text-center">
                    {loading === true ? (
                      <button
                        className="btn btn-primary btn-small w-25 rounded-5 text-uppercase"
                        type="button"
                        disabled
                      >
                        <span
                          className="spinner-border spinner-border-sm "
                          role="status"
                          aria-hidden="true"
                        ></span>
                        <span className="sr-only">Loading...</span>
                      </button>
                    ) : otpBox ? (
                      <button
                        className="btn btn-primary btn-small w-25 rounded-5 text-uppercase"
                        type="submit"
                      >
                        Sign Up
                      </button>
                    ) : (
                      <button
                        className="btn btn-primary btn-small w-25 rounded-5 text-uppercase"
                        type="submit"
                      >
                        Send otp
                      </button>
                    )}
                  </div>
                  <p className="font-size-4 text-center heading-default-color">
                    Already have an account?
                    <Link to="" className="text-primary" onClick={props.login}>
                      Login
                    </Link>
                  </p>
                </form>
                {/* END SIGNUP FORM */}
              </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
}
