import React, { useState } from "react";
import Pagination from "../common/pagination";
import Loader from "../common/loader";
import { Link } from "react-router-dom";
import PreviewEmail from "./emailPreview";
import SendMailForm from "../forms/user/sendMailForm";
import { BsFillPencilFill } from "react-icons/bs";
import { ImCross } from "react-icons/im";
import moment from "moment";
const EmailList = ({
  data,
  isLoading,
  totalData,
  setCurrentPage,
  currentPage,
  nPages,
  setApiCall,
  emailType,
  setEmailType,
  email,
}) => {
  const [emailId, setEmailId] = useState();
  const [sentEmail, setSentEmail] = useState("no");
  let user_type = localStorage.getItem("userType");

  /*Function to decode the email subject */
  function decodeEmailSubject(encodedSubject) {
    return decodeURIComponent(
      encodedSubject
        .replace(/=\?UTF-8\?Q\?/g, "")
        .replace(/\?=/g, "")
        .replace(/=([0-9A-F]{2})/g, (_, p1) =>
          String.fromCharCode(parseInt(p1, 16))
        )
    );
  }
  return (
    <div className="emails">
      <div className="email-list">
        <div
          className={`btn-group mb-5 `}
          role="group"
          aria-label="Basic example"
        >
          <div
            className={` ${
              user_type === "user" || user_type === "company" ? "" : "row"
            }`}
          >
            <div
              className={` ${
                user_type === "user" || user_type === "company" ? "" : "col-11"
              }`}
            >
              <button
                type="button"
                className={
                  emailType === "SENT"
                    ? "btn btn-primary"
                    : "btn btn-outline-primary"
                }
                onClick={() => setEmailType("SENT")}
              >
                Sent
              </button>
              <button
                type="button"
                className={
                  emailType === "INBOX"
                    ? "btn btn-primary"
                    : "btn btn-outline-primary"
                }
                onClick={() => setEmailType("INBOX")}
              >
                Inbox
              </button>
            </div>
            <div
              className={`${
                user_type === "user" || user_type === "company" ? "d-none" : ""
              } col-1`}
            >
              <button
                type="button"
                className={"btn btn-outline-primary "}
                onClick={() => {
                  sentEmail === "yes"
                    ? setSentEmail("no")
                    : setSentEmail("yes");
                }}
              >
                {sentEmail === "yes" ? (
                  <div>
                    Cancel <ImCross />
                  </div>
                ) : (
                  <div>
                    Compose <BsFillPencilFill />
                  </div>
                )}
              </button>
            </div>
          </div>
        </div>
        <div className="bg-white shadow-8 datatable_div pt-7 rounded pb-8 px-2 row m-0 p-5 ">
          <div className="table-responsive main_table_div col-md-6 ">
            {isLoading ? (
              <Loader />
            ) : (
              <>
                <table className="table table-striped main_data_table ">
                  <thead>
                    <tr className="">
                      <th
                        scope="col"
                        className="border-0 font-size-4 font-weight-normal"
                      >
                        <Link
                          to={""}
                          // onClick={() => {
                          //   handleSort("contact_no");
                          //   props.setpageNo(1);
                          // }}
                          className="text-gray"
                          title="Sort by Contact"
                        >
                          Sender
                        </Link>
                      </th>
                      <th
                        scope="col"
                        className=" border-0 font-size-4 font-weight-normal"
                      >
                        <Link
                          to={""}
                          // onClick={() => {
                          //   handleSort("name");
                          //   props.setpageNo(1);
                          // }}
                          className="text-gray"
                          title="Sort by Name"
                        >
                          Subject
                        </Link>
                      </th>

                      {
                        // props.heading === "Dashboard" ? (
                        //   ""
                        // ) :
                        <th
                          scope="col"
                          className="border-0 font-size-4 font-weight-normal"
                        >
                          <Link
                            to={""}
                            // onClick={() => {
                            //   handleSort("interested_in");
                            //   props.setpageNo(1);
                            // }}
                            className="text-gray"
                            title="Sort by interested in"
                          >
                            Date
                          </Link>
                        </th>
                      }

                      {/*                     
                    {props.heading === "Dashboard" ||
                    user_type === "company" ? (
                      ""
                    ) : (
                      <th
                        scope="col"
                        className="border-0 font-size-4 font-weight-normal"
                      >
                        Action
                      </th>
                    )} */}
                    </tr>
                  </thead>
                  <tbody>
                    {/* Map function to show the data in the list*/}
                    {totalData === 0 || data.length === 0 ? (
                      <tr>
                        <th colSpan={3} className="bg-white text-center">
                          No Data Found
                        </th>
                      </tr>
                    ) : (
                      (data || []).map((email, i) => (
                        <React.Fragment key={i}>
                          <tr
                            className={` applicant_row`}
                            key={i}
                            style={
                              emailId === email.msgno
                                ? { backgroundColor: "#f0f5f6" }
                                : null
                            }
                          >
                            {
                              // props.heading === "Dashboard" ? (
                              //   ""
                              // ) :
                              <td className=" py-5">
                                {email.sender === null ? (
                                  <p className="font-size-3  mb-0">N/A</p>
                                ) : (
                                  <h3 className="font-size-3 font-weight-normal text-black-2 mb-0">
                                    <p className="text-gray font-size-2 m-0">
                                      <Link
                                        className="text-dark"
                                        to={`mailto:${email.sender}`}
                                      >
                                        {email.seen === "1" ||
                                        email.seen === 1 ? (
                                          email.sender
                                        ) : (
                                          <b>{email.sender}</b>
                                        )}
                                      </Link>
                                    </p>
                                  </h3>
                                )}
                              </td>
                            }
                            <td className="py-5 ">
                              {email.Subject === null ||
                              email.Subject === "" ||
                              email.Subject === undefined ||
                              email.Subject === "undefined" ? (
                                <p className="font-size-3 mb-0">N/A</p>
                              ) : (
                                <Link
                                  title="Emil Preview"
                                  onClick={() => {
                                    setEmailId(email.msgno);
                                    setApiCall(true);
                                    setSentEmail("no");
                                  }}
                                >
                                  <p className="m-0 text-truncate">
                                    {email.seen === "1" || email.seen === 1 ? (
                                      email.Subject.includes("=?UTF-8?Q?") ? (
                                        decodeEmailSubject(email.Subject)
                                      ) : (
                                        email.Subject
                                      )
                                    ) : (
                                      <b>
                                        {email.Subject.includes("=?UTF-8?Q?")
                                          ? decodeEmailSubject(email.Subject)
                                          : email.Subject}
                                      </b>
                                    )}
                                  </p>
                                </Link>
                              )}
                            </td>

                            {
                              // props.heading === "Dashboard" ? (
                              //   ""
                              // ) :
                              <td className=" py-5">
                                {email.date === null || email.date === "" ? (
                                  <p className="font-size-2  mb-0">N/A</p>
                                ) : (
                                  <p className=" font-weight-normal text-black-2 font-size-2 mb-0 ">
                                    {email.seen === "1" || email.seen === 1 ? (
