                        </label>
                        <div className="position-relative">
                          <input
                            name="password"
                            value={state.password || ""}
                            onChange={onInputChange}
                            type="password"
                            className={
                              errors.password
                                ? "form-control border border-danger"
                                : "form-control"
                            }
                            id="password"
                            placeholder="Enter password"
