import moment from "moment";
import React, { useEffect, useState } from "react";
import { Modal } from "react-bootstrap";
import useValidation from "../../common/useValidation";
// import { CKEditor } from "ckeditor4-react";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import {
  AddCompany,
  EmployerDetails,
  GetFilter,
  // AddEmployerPermission,
} from "../../../api/api";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Permissions from "../../json/emailPermisionJson";
function CompanyDetails(props) {
  // const [editorValue, setEditorValue] = useState("");

  // const handleChange = (value) => {
  //   setEditorValue(value);
  // };
  // console.log(editorValue, "Data");
  const [loading, setLoading] = useState(false);
  const [imgError, setImgError] = useState("");
  let [Json, setJson] = useState([]);
  let encoded;
  const user_type = localStorage.getItem("userType");

  /*Function to get thejSon */
  const JsonData = async () => {
    try {
      let Json = await GetFilter();
      if (Json.data.message === "No data found") {
        setJson([]);
      } else {
        setJson(Json.data.data);
      }
      setJson(Json.data.data);
    } catch (err) {
      console.log(err);
    }
  };
  /* Functionality to close the modal */
  const close = () => {
    setState(initialFormState);
    setErrors("");
    setImgError("");
    setLoading(false);
    props.close();
  };
  // COMPANY DETAIL VALIDATION

  // INITIAL STATE ASSIGNMENT
  const initialFormState = {
    company_name: "",
    industry: "",
    corporation: "",
    alias: "",
    website_url: "",
    company_start_date: "",
    company_size: "",
    about: "",
    vacancy_for_post: "",
    franchise: "",
    logo: "",
    permission: props.employerId === "0" ? JSON.stringify(Permissions) : null,
  };
  // VALIDATION CONDITIONS
  const validators = {
    company_name: [
      (value) =>
        value === "" ||
        value === null ||
        value === undefined ||
        value.trim() === ""
          ? "Company name is required"
          : value.length < 2
          ? "Company Name should have 2 or more letters"
          : // : /[^A-Za-z 0-9]/g.test(value)
            // ? "Cannot use special character "
            "",
    ],
    industry: [
      (value) =>
        value === "" || value === null || value === undefined
          ? "Industry is required"
          : "",
    ],
    company_size: [
      (value) =>
        value === "" ||
        value === null ||
        value === undefined ||
        value.trim() === ""
          ? "Company Size is required"
          : /[^A-Za-z 0-9]/g.test(value)
          ? "Cannot use special character "
          : value === "0" || value === 0
          ? "Company Size can not be zero"
          : "",
    ],
    vacancy_for_post: [
      (value) =>
        value === "" ||
        value === null ||
        value === undefined ||
        value.trim() === ""
          ? "Vacancy is required"
          : /[^A-Za-z 0-9]/g.test(value)
          ? "Cannot use special character "
          : "",
    ],

    // franchise: [
    //   (value) =>
    //     value === "" || value === null || value === undefined
    //       ? ""
    //       : /[^A-Za-z 0-9]/g.test(value)
    //       ? "Cannot use special character "
    //       : /[-]?\d+(\.\d+)?/.test(value)
    //       ? "franchise can not have a number."
    //       : "",
    // ],
  };
  // API CALL
  const EmployerData = async () => {
    try {
      let userData = await EmployerDetails(props.employerId);
      if (
        userData === undefined ||
        props.employerId === "0" ||
        props.employerId === undefined ||
        props.employerId.length === 0 ||
        userData.data.company_detail.length === 0
      ) {
        setState(initialFormState);
      } else {
        setState(userData.data.company_detail[0]);
      }
    } catch (err) {
      console.log(err);
    }
  };
  useEffect(() => {
    JsonData();
    if (props.employerId !== "0") {
      EmployerData();
    }
  }, [props]);
  // CUSTOM VALIDATIONS IMPORT
  const { state, setErrors, setState, onInputChange, errors, validate } =
    useValidation(initialFormState, validators);

  /*Function to convert file to base64 */
  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.addEventListener("load", () => {
        resolve({ base64: fileReader.result });
      });
      fileReader.readAsDataURL(file);
      fileReader.onerror = (error) => {
        reject(error);
      };
    });
  };
  /*Onchange function of Logo */
  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (event) => {
      const img = new Image();
      img.onload = () => {
        // eslint-disable-next-line
        if (file.size > 1024 * 100 === true) {
          setImgError("Image size can't be more then 100 kb");
        } else {
          setImgError("");
          setState({ ...state, logo: event.target.result });
        }
      };
      img.src = event.target.result;
    };

    // Read the file as a data URL
    reader.readAsDataURL(file);
    encoded = await convertToBase64(file);
    let base64Name = encoded.base64;
    setState({ ...state, logo: base64Name });
  };
  // COMPANY DETAIL SUBMIT BUTTON
  const onCompanyDetailClick = async (event) => {
    event.preventDefault();
    if (validate() && imgError === "") {
      setLoading(true);
      try {
        let responseData = await AddCompany(state);
        if (responseData.message === "Employer data inserted successfully") {
          try {
            // await AddEmployerPermission(Permissions);
            toast.success("Company Added successfully", {
              position: toast.POSITION.TOP_RIGHT,
              autoClose: 1000,
            });
            props.setApiCall(true);
            return close();
          } catch (err) {
            console.log(err);
          }
        }
        if (responseData.message === "Employer data updated successfully") {
          toast.success("Company Updated successfully", {
            position: toast.POSITION.TOP_RIGHT,
            autoClose: 1000,
          });
          props.setApiCall(true);
          return close();
        }
      } catch (err) {
        console.log(err);
        setLoading(false);
      }
    } else {
      setLoading(false);
    }
  };
  // END COMPANY DETAIL VALIDATION
  /*Industry Json for not having same data */
  const Industry = Json.Industry
    ? Json.Industry.filter(
        (thing, index, self) =>
          index === self.findIndex((t) => t.value === thing.value)
      )
    : [];
  /*Corporation Json for not having same data */
  // const Corporation = Json.Corporation
  //   ? Json.Corporation.filter(
  //       (thing, index, self) =>
  //         index === self.findIndex((t) => t.value === thing.value)
  //     )
  //   : [];
  return (
    <>
      <Modal
        show={props.show}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <button
          type="button"
          className="circle-32 btn-reset bg-white pos-abs-tr mt-md-n6 mr-lg-n6 focus-reset z-index-supper"
          data-dismiss="modal"
          onClick={close}
        >
          <i className="fas fa-times"></i>
        </button>
        <div className="bg-white rounded h-100 px-11 pt-7">
          <form onSubmit={onCompanyDetailClick}>
            <h5 className="text-center pt-2 mb-7">
              {user_type === "company" ? "Company Details" : "Employer Details"}
            </h5>
            <input type="hidden" value={state.company_id || ""} />
            <div className="row">
              <ReactQuill value={state.alias} onChange={onInputChange} />
              <div className="form-group col-md-6">
                <label
                  htmlFor="company_name"
                  className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                >
                  Companys Legal Name
                  <span className="text-danger">*</span>:
                </label>
                <input
                  type="text"
                  maxLength={60}
                  name="company_name"
                  value={state.company_name || ""}
                  onChange={onInputChange}
                  className={
                    errors.company_name
                      ? "form-control border border-danger"
                      : "form-control"
                  }
                  placeholder="Companys Legal Name"
                  id="company_name"
                />
                {/*----ERROR MESSAGE FOR COMPANY NAME----*/}
                {errors.company_name && (
                  <span
                    key={errors.company_name}
                    className="text-danger font-size-3"
                  >
                    {errors.company_name}
                  </span>
                )}
              </div>
              <div className="form-group col-md-6">
                <label
                  htmlFor="franchise"
                  className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                >
                  Operating Name:
                </label>
                <div className="position-relative">
                  <input
                    maxLength={60}
                    name="franchise"
                    value={state.franchise || ""}
                    onChange={onInputChange}
                    type="text"
                    className={
                      errors.franchise
                        ? "form-control border border-danger"
                        : "form-control"
                    }
                    placeholder="Operating Name"
                    id="franchise"
                  />
                  {/*----ERROR MESSAGE FOR FRANCHISE----*/}
                  {errors.franchise && (
                    <span
                      key={errors.franchise}
                      className="text-danger font-size-3"
                    >
                      {errors.franchise}
                    </span>
                  )}
                </div>
              </div>
              <div className="form-group col-md-6">
                <label
                  htmlFor="industry"
                  className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                >
                  Industry<span className="text-danger">*</span>:
                </label>
                <select
                  name="industry"
                  value={state.industry || ""}
                  onChange={onInputChange}
                  className={
                    errors.industry
                      ? "text-capitalize form-control px-5 border border-danger"
                      : "text-capitalize form-control px-5"
                  }
                  id="industry"
                >
                  <option value={""}>Industry company belongs to</option>
                  {(Industry || []).map((industry) => (
                    <option key={industry.id} value={industry.value}>
                      {industry.value}
                    </option>
                  ))}
                </select>
                {/*----ERROR MESSAGE FOR INDUSTRY ----*/}
                {errors.industry && (
                  <span
                    key={errors.industry}
                    className="text-danger font-size-3"
                  >
                    {errors.industry}
                  </span>
                )}
              </div>
              <div className="form-group col-md-6">
                <label
                  htmlFor="contactperson"
                  className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                >
                  Sole Proprietor:
                </label>
                <input
                  type="text"
                  placeholder="Sole Proprietor"
                  maxLength={60}
                  name="corporation"
                  value={state.corporation || ""}
                  onChange={onInputChange}
                  className={
                    errors.corporation
                      ? "text-capitalize form-control border border-danger"
                      : "text-capitalize form-control"
                  }
                  id="corporation"
                />
                {/* <select
                  type="text"
                  placeholder="Contact Person "
                  maxLength={60}
                  name="corporation"
                  value={state.corporation || ""}
                  onChange={onInputChange}
                  className={
                    errors.corporation
                      ? "text-capitalize form-control border border-danger"
                      : "text-capitalize form-control"
                  }
                  id="corporation"
                >
                  <option value={""}>Company corporation</option>
                  {(Corporation || []).map((data) => (
                    <option key={data.id} value={data.value}>
                      {data.value}
                    </option>
                  ))}
                </select> */}
                {/*----ERROR MESSAGE FOR CORPORATION----*/}
                {errors.corporation && (
                  <span
                    key={errors.corporation}
                    className="text-danger font-size-3"
                  >
                    {errors.corporation}
                  </span>
                )}
              </div>
              <div className="form-group col-md-6">
                <label
                  htmlFor="alias"
                  className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                >
                  Alias:
                </label>
                <div className="position-relative">
                  <input
                    type="text"
                    placeholder="Alias"
                    maxLength={60}
                    name="alias"
                    value={state.alias || ""}
                    onChange={onInputChange}
                    className={
                      errors.alias
                        ? "form-control border border-danger"
                        : "form-control"
                    }
                    id="alias"
                  />
                  {/*----ERROR MESSAGE FOR ALIAS----*/}
                  {errors.alias && (
                    <span
                      key={errors.alias}
                      className="text-danger font-size-3"
                    >
                      {errors.alias}
                    </span>
                  )}
                </div>
              </div>
              <div className="form-group col-md-6">
                <label
                  htmlFor="company_start_date"
                  className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                >
                  Company Start Date:
                </label>

                <input
                  max={moment().format("DD-MM-YYYY")}
                  type="date"
                  name="company_start_date"
                  onKeyDownCapture={(e) => e.preventDefault()}
                  value={state.company_start_date || ""}
                  onChange={onInputChange}
                  className={
                    errors.company_start_date
                      ? "form-control  border border-danger coustam_datepicker"
                      : "form-control coustam_datepicker"
                  }
                  placeholder="company_start_date"
                  id="company_start_date"
                />
                {/*----ERROR MESSAGE FOR COMPANY SART DATE----*/}
                {errors.company_start_date && (
                  <span
                    key={errors.company_start_date}
                    className="text-danger font-size-3"
                  >
                    {errors.company_start_date}
                  </span>
                )}
              </div>
              <div className="form-group col-md-6">
                <label
                  htmlFor="company_size"
                  className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                >
                  No. of working official's
                  <span className="text-danger">*</span>:
                </label>
                <div className="position-relative">
                  <input
                    maxLength={60}
                    name="company_size"
                    value={state.company_size || ""}
                    onChange={onInputChange}
                    type="number"
                    className={
                      errors.company_size
                        ? "form-control border border-danger"
                        : "form-control"
                    }
                    placeholder="No. of working official's"
                    id="company_size"
                    min={0}
                  />
                  {/*----ERROR MESSAGE FOR COMPANY SIZE----*/}
                  {errors.company_size && (
                    <span
                      key={errors.company_size}
                      className="text-danger font-size-3"
                    >
                      {errors.company_size}
                    </span>
                  )}
                </div>
              </div>
              <div className="form-group col-md-6">
                <label
                  htmlFor="website_url"
                  className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                >
                  Website URL:
                </label>
                <input
                  type="text"
                  placeholder="Website Url"
                  maxLength={60}
                  name="website_url"
                  value={state.website_url || ""}
                  onChange={onInputChange}
                  className={
                    errors.website_url
                      ? "form-control border border-danger"
                      : "form-control"
                  }
                  id="website_url"
                />
                {/*----ERROR MESSAGE FOR WEBSITE URL----*/}
                {errors.website_url && (
                  <span
                    key={errors.website_url}
                    className="text-danger font-size-3"
                  >
                    {errors.website_url}
                  </span>
                )}
              </div>
              <div className="form-group col-md-6">
                <label
                  htmlFor="vacancy_for_post"
                  className="font-size-4 text-black-2 font-weight-semibold line-height-reset"
                >
                  Number of Advertisements Posted
                  <span className="text-danger">*</span>:
                </label>
                <div className="position-relative">
                  <input
                    maxLength={60}
                    name="vacancy_for_post"
                    value={state.vacancy_for_post || ""}
                    onChange={onInputChange}
                    type="text"
                    className={
                      errors.vacancy_for_post
                        ? "form-control border border-danger"
                        : "form-control"
                    }
                    placeholder="Number of Advertisements Posted"
                    id="vacancy_for_post"
                  />
                  {/*----ERROR MESSAGE FOR VACANCY FOR POST----*/}
                  {errors.vacancy_for_post && (
                    <span
                      key={errors.vacancy_for_post}
                      className="text-danger font-size-3"
                    >
                      {errors.vacancy_for_post}
                    </span>
                  )}
                </div>
              </div>
              <div className="form-group col-md-6">
                <label
                  htmlFor="about"
                  className="font-size-3 text-black-2 font-weight-semibold line-height-reset mb-0"
                >
                  Company Logo:
                </label>
                <div className="position-relative">
                  <input
                    type="file"
                    className="form-control"
                    accept=" image/png,image/jpeg,image/jpg,image/gif"
                    onChange={handleFileChange}
                  />
                </div>
                <small className="text-danger">{imgError}</small>
              </div>
            </div>
            <div className="row">
              <div className="form-group col-md-12">
                <label
                  htmlFor="about"
                  className="font-size-3 text-black-2 font-weight-semibold line-height-reset mb-0"
                >
                  About:
                </label>
                <div className="position-relative">
                  <div
                    sm="12"
                    className={
                      errors.about
                        ? "border border-danger rounded overflow-hidden"
                        : "border rounded overflow-hidden"
                    }
                  >
                    {/* <CKEditor
                      type={"classic"}
                      name={"about"}
                      id={"about"}
                      data={state.about}
                      value={state.about}
                      onChange={onInputChange}
                      initData="About Company"
                    /> */}
                    <textarea
                      name="about"
                      value={state.about || ""}
                      onChange={onInputChange}
                      className={
                        errors.about
                          ? "form-control border border-danger"
                          : "form-control"
                      }
                      id="about"
                      placeholder="Company Description"
                    ></textarea>
                  </div>
                  {/*----ERROR MESSAGE FOR DESRIPTION----*/}
                  {errors.about && (
                    <span
                      key={errors.about}
                      className="text-danger font-size-3"
                    >
                      {errors.about}
                    </span>
                  )}
                </div>
              </div>
            </div>
            <div className="form-group mb-8 text-center">
              {loading === true ? (
                <button
                  className="btn btn-primary btn-small w-25 rounded-5 text-uppercase"
                  type="button"
                  disabled
                >
                  <span
                    className="spinner-border spinner-border-sm "
                    role="status"
                    aria-hidden="true"
                  ></span>
                  <span className="sr-only">Loading...</span>
                </button>
              ) : (
                <button
                  className="btn btn-primary btn-medium w-25 rounded-5 text-uppercase"
                  type="submit"
                >
                  Submit
                </button>
              )}
            </div>
          </form>
        </div>
      </Modal>
    </>
  );
}

export default CompanyDetails;
