import React, {useEffect, useState} from "react";
// import FilterJson from "../json/filterjson";
// import Select from "react-select";
import {useNavigate, useLocation} from "react-router-dom";
import Filterbox from "./filterbox";
import states from "../json/states";
import {getJson} from "../../api/api";
function SearchForm() {
    const location = useLocation();
    const path = location.pathname;
    const searchParams = new URLSearchParams(location.search);
    const search = searchParams.get("search");
    const country = searchParams.get("country");
    const user_type = localStorage.getItem("userType");
    let [Json, setJson] = useState([]);
    let [state, setState] = useState({
        search: search ? search : "",
        country: "",
        country_value: country ? country : ""
    });
    /*Function to get the jSon */
    const JsonData = async () => {
        try {
            let Json = await getJson();
            setJson(Json);
        } catch (err) {
            console.log(err);
        }
    };
    let navigate = useNavigate();
    /*Function to set data to the search job by country */
    const onSelectChange = (option) => {
        setState({
            ...state,
            country_value: option.target.value
        });
    };

    /*Function to redender the data in the option of the select box*/
    useEffect(() => {
        JsonData();
        const options = (Object.keys(states) || []).map((option) => ({value: option, label: option}));
        setState({
            ...state,
            country: options
        });
    }, [states]);

    /*Onclick Function to search */
    const Onsearch = () => {
        if (user_type === "company") {
            navigate(`/empsearch?search=${
                state.search
            }`);
            setState({
                ...state,
                search: ""
            });
        } else {
            navigate(`/jobs?search=${
                state.search
            }&country=${
                state.country_value
            }`);
            setState({
                ...state,
                search: "",
                country: ""
            });
        }
    };
    return (
        <form // action={user_type === "company" ? "/managejobs" : "/jobs"}
            className="search-form"
            data-aos="fade-up"
            data-aos-duration="800"
            data-aos-once="true"
        >
            <div className=" bg-white job_search_main_form rounded-70 shadow-7 pr-15 py-7 pl-12">
                <div className="filter-search-form-2">
                    <div className="filter-inputs">
                        <div className="form-group position-relative">
                            <input className="form-control focus-reset pl-13" type="text" id="keyword" name="search"
                                value={
                                    state.search
                                }
                                onChange={
                                    (e) => setState({
                                        ...state,
                                        search: e.target.value
                                    })
                                }
                                placeholder="Type Job title, keywords"/>
                            <span className="h-100 w-px-50 pos-abs-tl d-flex align-items-center justify-content-center font-size-6">
                                <i className="icon icon-zoom-2 text-primary font-weight-bold"></i>
                            </span>
                        </div>
                        {/* <!-- select-city starts --> */}
                        {
                        user_type === "company" ? null : (
                            <div className="form-group position-relative">
                                {/* <Select
                  options={"" || state.country}
                  name="country"
                  id="country"
                  onChange={onSelectChange}
                  className="bg-white pl-13"
                />

                <span className="h-100 w-px-50 pos-abs-tl d-flex align-items-center justify-content-center font-size-6">
                  <i className="icon icon-pin-3 text-primary font-weight-bold"></i>
                </span> */}
                                <select className="form-control"
                                    onChange={onSelectChange}>
                                    <option value="">Select location</option>
                                    {
                                    (state.country || []).map((item, index) => {
                                        return (
                                            <option value={
                                                    item.value
                                                }
                                                key={index}>
                                                {
                                                item.label
                                            }</option>
                                        )
                                    })
                                } </select>
                            </div>
                        )
                    } </div>
                    {/* <!-- ./select-city ends --> */}
                    <button onClick={
                            /*path === "/jobs" ? null :*/
                            Onsearch
                        }
                        className="btn btn-primary line-height-reset text-uppercase main_search_button_home"
                        type="button">
                        Search
                    </button>
                </div>
                {
                path === "/jobs" || path === "/managejobs" || path === "/response" || user_type === "company" ? null : (
                    <div className="row m-0 job_filter_block mt-5">
                        {/* <Filterbox
            name="country"
            filterheading=" Jobs by Location"
            filterjson={filterjson.location}
            type={"location"}
          /> */}
                        <Filterbox name="category" filterheading=" Jobs by Category"
                            filterjson={
                                Json.Category
                            }
                            type={"category"}/>
                    </div>
                )
            } </div>
        </form>
    );
}
export default SearchForm;
