/*Api to Get agent*/
export const GetAgent = async (id, search, page, limit, column, Order) => {
  const response = await axios.post(
    `${API_URL}agent/getAgent`,
    {
      page: page,
      search: search,
      limit: limit,
      column_name: column,
      sort_order: Order,
      id: id,
    },
    {
      headers: {
        "Content-Type": "application/json",
        Authorization: Token,
      },
    }
  );
  return response.data;
};
